pkg load image;% load the imageimg = imread(fullfile('input','ps1-input0-noise.png'));%figure, imshow(img);% detect the edgesedges = edge(img, 'canny');%figure, imshow(edges);%{% Apply Hough transform using hough_line();theta1 = linspace(-pi/2, pi/2, 181);[hough1, rho1] = hough_line(edges, theta1);figure, imagesc(theta1, rho1, hough1), title('Hough accumulator');%}% Apply Hough transform using houghtf();hough2 = houghtf(edges);theta2 = linspace(-90, 90, size(hough2, 2));r = size(hough2, 1);rho2 = linspace(-(r-1)/2, (r-1)/2, r);figure, imagesc(theta2, rho2, hough2), title('Hough accumulator');%[row, col] = immaximas(hough2, 1, 100);% hold on; plot(theta2(row), rho2(col), 'rs'); hold off;